class CreateFamily implements UseCase<Family, CreateFamilyParams> {
  final FamilyRepository repository;

  const CreateFamily(this.repository);

  @override
  Future<Either<Failure, Family>> call(CreateFamilyParams params) async {
    // First, create the family and get the family ID
    final result = await repository.createFamily(params.familyName);

    // Then, load the created family to get the full Family object
    return result.fold(
      (failure) => Left(failure),
      (familyId) async {
        final familyResult = await repository.getCurrentFamily();
        return familyResult.fold(
          (failure) => Left(failure),
          (family) => family != null 
              ? Right(family) 
              : const Left(CacheFailure('Failed to load created family')),
        );
      },
    );
  }
}

class CreateFamilyParams extends Equatable {
  final String familyName;

  const CreateFamilyParams({required this.familyName});

  @override
  List<Object> get props => [familyName];

  @override
  bool get stringify => true;
}

