final sl = GetIt.instance;

Future<void> initAuthFeature() async {
  // ViewModel
  sl.registerFactory(
    () => AuthViewModel(
      checkAuthStatus: sl(),
      signInWithEmailAndPassword: sl(),
      registerWithEmailAndPassword: sl(),
      signInWithGoogle: sl(),
      signInWithApple: sl(),
      sendOtpToPhoneNumber: sl(),
      verifyOtp: sl(),
      signOut: sl(),
      sendPasswordResetEmail: sl(),
      deleteAccount: sl(),
    ),
  );

  // Use cases
  sl.registerLazySingleton(() => CheckAuthStatus(sl()));
  sl.registerLazySingleton(() => SignInWithEmailAndPassword(sl()));
  sl.registerLazySingleton(() => RegisterWithEmailAndPassword(sl()));
  sl.registerLazySingleton(() => SignInWithGoogle(sl()));
  sl.registerLazySingleton(() => SignInWithApple(sl()));
  sl.registerLazySingleton(() => SendOtpToPhoneNumber(sl()));
  sl.registerLazySingleton(() => VerifyOtp(sl()));
  sl.registerLazySingleton(() => SignOut(sl()));
  sl.registerLazySingleton(() => SendPasswordResetEmail(sl()));
  sl.registerLazySingleton(() => DeleteAccount(sl()));

  // Repository
  sl.registerLazySingleton<AuthRepository>(
