import 'package:family_tree_firebase/features/family/domain/usecases/create_family.dart';
import 'package:family_tree_firebase/features/family/domain/usecases/generate_invite_code.dart';
import 'package:family_tree_firebase/features/family/domain/usecases/get_current_family.dart';
import 'package:family_tree_firebase/features/family/domain/usecases/join_family.dart';
import 'package:family_tree_firebase/features/family/domain/usecases/validate_invite_code.dart';

class FamilyViewModel extends BaseViewModel {
  final CreateFamily createFamily;
  final JoinFamily joinFamily;
  final GetCurrentFamily getCurrentFamily;
  final GenerateInviteCode generateInviteCode;
  final ValidateInviteCode validateInviteCode;

  Family? _currentFamily;
  String? _generatedInviteCode;
  String? _errorMessage;
  bool _isLoading = false;
  bool _isInviteCodeValid = false;

  Family? get currentFamily => _currentFamily;
  String? get generatedInviteCode => _generatedInviteCode;
  String? get errorMessage => _errorMessage;
  bool get isInviteCodeValid => _isInviteCodeValid;
  @override
  bool get isLoading => _isLoading;

  FamilyViewModel({
    required this.createFamily,
    required this.joinFamily,
    required this.getCurrentFamily,
    required this.generateInviteCode,
    required this.validateInviteCode,
  });

  // Create a new family
  Future<bool> createNewFamily(String familyName) async {
    setLoading(true);
    try {
      final result = await createFamily(CreateFamilyParams(name: familyName));
      return result.fold(
        (failure) {
          _errorMessage = failure.message;
          notifyListeners();
          return false;
        },
        (family) {
          _currentFamily = family;
          notifyListeners();
          return true;
        },
      );
    } catch (e) {
      _errorMessage = 'Failed to create family: $e';
      notifyListeners();
      return false;
    } finally {
      setLoading(false);
    }
  }

  // Join an existing family
  Future<bool> joinFamily(String inviteCode) async {
    setLoading(true);
    try {
      final result = await joinFamily(JoinFamilyParams(inviteCode: inviteCode));
      return result.fold(
        (failure) {
          _errorMessage = failure.message;
          notifyListeners();
          return false;
        },
        (family) {
          _currentFamily = family;
          notifyListeners();
          return true;
        },
      );
    } catch (e) {
      _errorMessage = 'Failed to join family: $e';
      notifyListeners();
      return false;
    } finally {
      setLoading(false);
    }
  }

  // Get current user's family
  Future<void> loadCurrentFamily() async {
    setLoading(true);
    try {
      final result = await getCurrentFamily(NoParams());
      result.fold(
        (failure) {
          _errorMessage = failure.message;
          _currentFamily = null;
        },
        (family) {
          _currentFamily = family;
          _errorMessage = null;
        },
      );
      notifyListeners();
    } catch (e) {
      _errorMessage = 'Failed to load family: $e';
      _currentFamily = null;
      notifyListeners();
    } finally {
      setLoading(false);
    }
  }

  // Generate a new invite code
  Future<String?> generateNewInviteCode() async {
    setLoading(true);
    try {
      final result = await generateInviteCode(NoParams());
      return result.fold(
        (failure) {
          _errorMessage = failure.message;
          _generatedInviteCode = null;
          notifyListeners();
          return null;
        },
        (inviteCode) {
          _generatedInviteCode = inviteCode;
          _errorMessage = null;
          notifyListeners();
          return inviteCode;
        },
      );
    } catch (e) {
      _errorMessage = 'Failed to generate invite code: $e';
      _generatedInviteCode = null;
      notifyListeners();
      return null;
    } finally {
      setLoading(false);
    }
  }

  // Validate an invite code
  Future<bool> checkInviteCode(String inviteCode) async {
    setLoading(true);
    try {
      final result = await validateInviteCode(
        ValidateInviteCodeParams(inviteCode: inviteCode),
      );
      return result.fold(
        (failure) {
          _errorMessage = failure.message;
          _isInviteCodeValid = false;
          notifyListeners();
          return false;
        },
        (isValid) {
          _isInviteCodeValid = isValid;
          _errorMessage = isValid ? null : 'Invalid invite code';
          notifyListeners();
          return isValid;
        },
      );
    } catch (e) {
      _errorMessage = 'Failed to validate invite code: $e';
      _isInviteCodeValid = false;
      notifyListeners();
      return false;
    } finally {
      setLoading(false);
    }
  }

  // Clear error message
  void clearError() {
    _errorMessage = null;
    notifyListeners();
  }

  @override
  void setLoading(bool loading) {
    _isLoading = loading;
    notifyListeners();
  }

  @override
  void setError({bool hasError = true, String? message}) {
    _errorMessage = message;
    notifyListeners();
  }
}

