import 'package:shared_preferences/shared_preferences.dart';

/// A service class to handle all shared preferences operations
class SharedPrefsManager {
  static SharedPrefsManager? _instance;
  static SharedPreferences? _prefs;

  // Private constructor
  SharedPrefsManager._();

  // Factory constructor to return the same instance (singleton)
  factory SharedPrefsManager() {
    _instance ??= SharedPrefsManager._();
    return _instance!;
  }

  /// Initialize SharedPreferences
  static Future<void> init() async {
    _prefs = await SharedPreferences.getInstance();
  }

  // Generic getter with proper type handling
  T _getValue<T>(String key, T defaultValue) {
    if (_prefs == null) return defaultValue;

    try {
      switch (T) {
        case String:
          final value = _prefs!.getString(key) as T?;
          return value ?? defaultValue;
        case int:
          final value = _prefs!.getInt(key) as T?;
          return value ?? defaultValue;
        case bool:
          final value = _prefs!.getBool(key) as T?;
          return value ?? defaultValue;
        case double:
          final value = _prefs!.getDouble(key) as T?;
          return value ?? defaultValue;
        case const (List<String>):
          final value = _prefs!.getStringList(key) as T?;
          return value ?? defaultValue;
        default:
          return defaultValue;
      }
    } catch (e) {
      return defaultValue;
    }
  }

  // Generic setter with proper type checking
  Future<bool> _setValue<T>(String key, T value) async {
    if (_prefs == null) return false;

    try {
      if (T == String && value is String) {
        return await _prefs!.setString(key, value);
      } else if (T == int && value is int) {
        return await _prefs!.setInt(key, value);
      } else if (T == bool && value is bool) {
        return await _prefs!.setBool(key, value);
      } else if (T == double && value is double) {
        return await _prefs!.setDouble(key, value);
      } else if (T == List<String> && value is List<String>) {
        return await _prefs!.setStringList(key, value);
      } else {
        return false;
      }
    } catch (e) {
      return false;
    }
  }

  // Auth related methods with proper null safety
  Future<bool> setAccessToken(String token) => _setValue<String>('access_token', token);
  String get accessToken => _getValue<String>('access_token', '');

  Future<bool> setRefreshToken(String token) => _setValue<String>('refresh_token', token);
  String get refreshToken => _getValue<String>('refresh_token', '');

  Future<bool> setUserId(String userId) => _setValue<String>('user_id', userId);
  String get userId => _getValue<String>('user_id', '');

  Future<bool> setUserEmail(String email) => _setValue<String>('user_email', email);
  String get userEmail => _getValue<String>('user_email', '');

  Future<bool> setUserName(String name) => _setValue<String>('user_name', name);
  String get userName => _getValue<String>('user_name', '');

  Future<bool> setUserPhone(String phone) => _setValue<String>('user_phone', phone);
  String get userPhone => _getValue<String>('user_phone', '');

  Future<bool> setUserProfilePic(String url) => _setValue<String>('user_profile_pic', url);
  String get userProfilePic => _getValue<String>('user_profile_pic', '');

  // App settings
  Future<bool> setDarkMode(bool isDark) => _setValue<bool>('is_dark_mode', isDark);
  bool get isDarkMode => _getValue<bool>('is_dark_mode', false);

  Future<bool> setFirstLaunch(bool isFirstLaunch) => _setValue<bool>('is_first_launch', isFirstLaunch);
  bool get isFirstLaunch => _getValue<bool>('is_first_launch', true);

  // Family related
  Future<bool> setFamilyId(String familyId) => _setValue<String>('family_id', familyId);
  String get familyId => _getValue<String>('family_id', '');

  Future<bool> setFamilyName(String name) => _setValue<String>('family_name', name);
  String get familyName => _getValue<String>('family_name', '');

  // Clear all data (logout)
  Future<bool> clearAll() async {
    if (_prefs == null) return false;
    return await _prefs!.clear();
  }

  // Clear specific data
  Future<bool> clearKey(String key) async {
    if (_prefs == null) return false;
    return await _prefs!.remove(key);
  }
}

import 'package:flutter/material.dart';

/// A service that handles navigation between screens in the app.
///
/// This service provides a global [GlobalKey] for the [NavigatorState] and
/// helper methods to navigate between screens.
class NavigationService {
  static final NavigationService _instance = NavigationService._internal();

  /// The global key for the navigator
  final GlobalKey<NavigatorState> navigatorKey = GlobalKey<NavigatorState>();

  /// Private constructor for the singleton pattern
  NavigationService();

  /// Get the current [BuildContext]
  BuildContext? get currentContext => navigatorKey.currentContext;

  /// Get the current [NavigatorState]
  NavigatorState? get _navigator => navigatorKey.currentState;

  /// Navigate to a named route
  ///
  /// [routeName] The name of the route to navigate to
  /// [arguments] Optional arguments to pass to the route
  /// Returns a [Future] that completes when the pushed route is popped
  Future<T?> navigateTo<T extends Object?>(String routeName, {Object? arguments}) {
    if (_navigator == null) {
      return Future.value(null);
    }
    return _navigator!.pushNamed<T>(
      routeName,
      arguments: arguments,
    );
  }

  /// Navigate to a named route and remove all previous routes
  ///
  /// [routeName] The name of the route to navigate to
  /// [arguments] Optional arguments to pass to the route
  /// [predicate] Optional predicate to determine which routes to remove
  /// Returns a [Future] that completes when the pushed route is popped
  Future<T?> navigateToAndRemoveUntil<T extends Object?>(
    String routeName, {
    Object? arguments,
    bool Function(Route<dynamic>)? predicate,
  }) {
    if (_navigator == null) {
      return Future.value(null);
    }
    return _navigator!.pushNamedAndRemoveUntil<T>(
      routeName,
      predicate ?? (route) => false,
      arguments: arguments,
    );
  }

  /// Navigate to a named route and remove the current route
  ///
  /// [routeName] The name of the route to navigate to
  /// [arguments] Optional arguments to pass to the route
  /// [result] Optional result to return when the previous route is popped
  /// Returns a [Future] that completes when the pushed route is popped
  Future<T?> navigateToAndReplace<T extends Object?, TO extends Object?>(
    String routeName, {
    Object? arguments,
    TO? result,
  }) {
    if (_navigator == null) {
      return Future.value(null);
    }
    return _navigator!.pushReplacementNamed<T, TO>(
      routeName,
      arguments: arguments,
      result: result,
    );
  }

  /// Go back to the previous screen
  void goBack<T extends Object?>([T? result]) {
    if (_navigator?.canPop() ?? false) {
      _navigator?.pop<T>(result);
    }
  }

  /// Show a dialog with the given widget
  ///
  /// [builder] A builder that returns the dialog widget
  /// [barrierDismissible] Whether the dialog can be dismissed by tapping outside
  /// [barrierColor] The color of the barrier
  /// [useSafeArea] Whether to wrap the dialog in a SafeArea
  /// [useRootNavigator] Whether to use the root navigator
  /// [routeSettings] Optional route settings
  /// Returns a [Future] that resolves when the dialog is dismissed
  Future<T?> showDialog<T>({
    required WidgetBuilder builder,
    bool barrierDismissible = true,
    Color? barrierColor = Colors.black54,
    bool useSafeArea = true,
    bool useRootNavigator = true,
    RouteSettings? routeSettings,
  }) async {
    final context = currentContext;
    if (context == null) return null;

    return await showDialog<T>(
      context: context,
      builder: builder,
      barrierDismissible: barrierDismissible,
      barrierColor: barrierColor,
      useSafeArea: useSafeArea,
      useRootNavigator: useRootNavigator,
      routeSettings: routeSettings,
    );
  }

  /// Show a bottom sheet with the given widget
  ///
  /// [builder] A builder that returns the bottom sheet widget
  /// [isScrollControlled] Whether the bottom sheet should take up the full screen height
  /// [useRootNavigator] Whether to use the root navigator
  /// [isDismissible] Whether the bottom sheet can be dismissed by swiping down
  /// [enableDrag] Whether the bottom sheet can be dragged up and down
  /// [backgroundColor] The background color of the bottom sheet
  /// [elevation] The elevation of the bottom sheet
  /// [shape] The shape of the bottom sheet
  /// [clipBehavior] The clip behavior of the bottom sheet
  /// [constraints] Optional constraints for the bottom sheet
  /// [routeSettings] Optional route settings
  /// Returns a [Future] that resolves when the bottom sheet is dismissed
  Future<T?> showBottomSheet<T>({
    required WidgetBuilder builder,
    bool isScrollControlled = false,
    bool useRootNavigator = false,
    bool isDismissible = true,
    bool enableDrag = true,
    Color? backgroundColor,
    double? elevation,
    ShapeBorder? shape,
    Clip? clipBehavior = Clip.none,
    BoxConstraints? constraints,
    RouteSettings? routeSettings,
  }) async {
    final context = currentContext;
    if (context == null) return null;

    return await showModalBottomSheet<T>(
      context: context,
      builder: builder,
      isScrollControlled: isScrollControlled,
      useRootNavigator: useRootNavigator,
      isDismissible: isDismissible,
      enableDrag: enableDrag,
      backgroundColor: backgroundColor,
      elevation: elevation,
      shape: shape,
      clipBehavior: clipBehavior,
      constraints: constraints,
      routeSettings: routeSettings,
    );
  }

  /// Show a snackbar with the given message
  void showSnackBar({
    required String message,
    Duration duration = const Duration(seconds: 4),
    SnackBarAction? action,
    SnackBarBehavior behavior = SnackBarBehavior.fixed,
    DismissDirection dismissDirection = DismissDirection.down,
    EdgeInsetsGeometry? margin,
    EdgeInsetsGeometry? padding,
    double? width,
    ShapeBorder? shape,
    Color? backgroundColor,
    double? elevation,
    double? actionOverflowThreshold,
    Color? closeIconColor,
    bool? showCloseIcon,
    VoidCallback? onVisible,
  }) {
    final context = currentContext;
    if (context == null) return;

    ScaffoldMessenger.of(context).showSnackBar(
      SnackBar(
        content: Text(message),
        duration: duration,
        action: action,
        behavior: behavior,
        dismissDirection: dismissDirection,
        margin: margin,
        padding: padding,
        width: width,
        shape: shape,
        backgroundColor: backgroundColor,
        elevation: elevation,
        actionOverflowThreshold: actionOverflowThreshold,
        closeIconColor: closeIconColor,
        showCloseIcon: showCloseIcon,
      ),
    );
  }
}

