import 'dart:async';
import 'package:flutter/foundation.dart';
import 'package:family_tree_firebase/core/base/base_viewmodel.dart';
import 'package:family_tree_firebase/features/home/domain/models/post_model.dart';
import 'package:family_tree_firebase/features/home/domain/models/story_model.dart';
import 'package:family_tree_firebase/features/home/domain/usecases/get_posts.dart';
import 'package:family_tree_firebase/features/home/domain/usecases/get_stories.dart';
import 'package:family_tree_firebase/features/home/domain/usecases/like_post.dart';
import 'package:family_tree_firebase/features/home/domain/usecases/add_comment.dart';

class HomeViewModel extends BaseViewModel {
  final GetPosts getPosts;
  final GetStories getStories;
  final LikePost likePostUseCase;
  final AddComment addCommentUseCase;

  List<PostModel> _posts = [];
  List<StoryModel> _stories = [];
  bool _isLoadingPosts = false;
  bool _isLoadingStories = false;
  String? _errorMessage;

  List<PostModel> get posts => _posts;
  List<StoryModel> get stories => _stories;
  bool get isLoadingPosts => _isLoadingPosts;
  bool get isLoadingStories => _isLoadingStories;
  bool get hasError => _errorMessage != null;
  String? get errorMessage => _errorMessage;

  HomeViewModel({
    required this.getPosts,
    required this.getStories,
    required this.likePostUseCase,
    required this.addCommentUseCase,
  });

  Future<void> loadInitialData() async {
    await Future.wait([
      loadPosts(),
      loadStories(),
    ]);
  }

  Future<void> loadPosts() async {
    _isLoadingPosts = true;
    notifyListeners();

    try {
      final result = await getPosts(NoParams());
      result.fold(
        (failure) {
          _errorMessage = failure.message;
          _posts = [];
        },
        (posts) {
          _posts = posts;
          _errorMessage = null;
        },
      );
    } catch (e) {
      _errorMessage = 'Failed to load posts: $e';
      _posts = [];
    } finally {
      _isLoadingPosts = false;
      notifyListeners();
    }
  }

  Future<void> loadStories() async {
    _isLoadingStories = true;
    notifyListeners();

    try {
      final result = await getStories(NoParams());
      result.fold(
        (failure) {
          _errorMessage = failure.message;
          _stories = [];
        },
        (stories) {
          _stories = stories;
          _errorMessage = null;
        },
      );
    } catch (e) {
      _errorMessage = 'Failed to load stories: $e';
      _stories = [];
    } finally {
      _isLoadingStories = false;
      notifyListeners();
    }
  }

  Future<void> likePost(String postId) async {
    try {
      final result = await likePostUseCase(LikePostParams(postId: postId));
      result.fold(
        (failure) => _errorMessage = failure.message,
        (success) {
          final index = _posts.indexWhere((post) => post.id == postId);
          if (index != -1) {
            final post = _posts[index];
            _posts[index] = post.copyWith(
              isLiked: !post.isLiked,
              likes: post.isLiked ? post.likes - 1 : post.likes + 1,
            );
            _errorMessage = null;
            notifyListeners();
          }
        },
      );
    } catch (e) {
      _errorMessage = 'Failed to like post: $e';
      notifyListeners();
    }
  }

  Future<void> addComment(String postId, String comment) async {
    try {
      final result = await addCommentUseCase(
        AddCommentParams(postId: postId, comment: comment),
      );

      result.fold(
        (failure) => _errorMessage = failure.message,
        (comment) {
          final index = _posts.indexWhere((post) => post.id == postId);
          if (index != -1) {
            final post = _posts[index];
            final updatedComments = List<Comment>.from(post.comments)..add(comment);
            _posts[index] = post.copyWith(comments: updatedComments);
            _errorMessage = null;
            notifyListeners();
          }
        },
      );
    } catch (e) {
      _errorMessage = 'Failed to add comment: $e';
      notifyListeners();
    }
  }

  String _mapFailureToMessage(Failure failure) {
    // Map different failure types to user-friendly messages
    return failure.toString();
  }

  void setCurrentUser(String userId) {
    _currentUserId = userId;
    notifyListeners();
  }

  @override
  void dispose() {
    // Clean up any resources if needed
    _posts.clear();
    _stories.clear();
    super.dispose();
  }
}

import 'package:cloud_firestore/cloud_firestore.dart';
import 'package:firebase_auth/firebase_auth.dart';
import 'package:family_tree_firebase/core/error/exceptions.dart';
import 'package:family_tree_firebase/features/family/data/models/family_model.dart';
import 'package:family_tree_firebase/features/family/domain/entities/family.dart';

abstract class FamilyRemoteDataSource {
  Future<Family> createFamily(String name, String adminId);
  Future<Family> joinFamily(String inviteCode, String userId);
  Future<Family?> getFamily(String familyId);
  Future<bool> checkFamilyMembership(String userId);
  Future<String> generateInviteCode(String familyId);
  Future<bool> validateInviteCode(String inviteCode);
}

class FamilyRemoteDataSourceImpl implements FamilyRemoteDataSource {
  final FirebaseFirestore firestore;
  final FirebaseAuth firebaseAuth;

  FamilyRemoteDataSourceImpl({
    required this.firestore,
    required this.firebaseAuth,
  });

  @override
  Future<Family> createFamily(String name, String adminId) async {
    try {
      // First create the family document
      final familyDoc = firestore.collection('families').doc();
      final family = FamilyModel(
        id: familyDoc.id,
        name: name,
        adminId: adminId,
        memberIds: [adminId],
        createdAt: DateTime.now(),
        updatedAt: DateTime.now(),
      );

      await familyDoc.set(family.toDocument());

      // Update user's family reference
      await firestore.collection('users').doc(adminId).update({
        'familyId': family.id,
        'updatedAt': FieldValue.serverTimestamp(),
      });

      return family;
    } catch (e) {
      throw ServerException('Failed to create family: $e');
    }
  }

  @override
  Future<Family> joinFamily(String inviteCode, String userId) async {
    try {
      // Find family by invite code
      final familyQuery = await firestore
          .collection('families')
          .where('inviteCode', isEqualTo: inviteCode)
          .limit(1)
          .get();

      if (familyQuery.docs.isEmpty) {
        throw NotFoundException(message: 'Invalid invite code');
      }

      final familyDoc = familyQuery.docs.first;
      final family = FamilyModel.fromDocument(familyDoc.data(), familyDoc.id);

      // Check if user is already a member
      if (family.memberIds.contains(userId)) {
        return family;
      }

      // Add user to family members
      await familyDoc.reference.update({
        'memberIds': FieldValue.arrayUnion([userId]),
        'updatedAt': FieldValue.serverTimestamp(),
      });

      // Update user's family reference
      await firestore.collection('users').doc(userId).update({
        'familyId': family.id,
        'updatedAt': FieldValue.serverTimestamp(),
      });

      return family.copyWith(
        memberIds: [...family.memberIds, userId],
      );
    } catch (e) {
      throw ServerException('Failed to join family: $e');
    }
  }

  @override
  Future<Family?> getFamily(String familyId) async {
    try {
      final doc = await firestore.collection('families').doc(familyId).get();
      if (!doc.exists) {
        return null;
      }
      return FamilyModel.fromDocument(doc.data()!, doc.id);
    } catch (e) {
      throw ServerException('Failed to get family: $e');
    }
  }

  @override
  Future<bool> checkFamilyMembership(String userId) async {
    try {
      final userDoc = await firestore.collection('users').doc(userId).get();
      final familyId = userDoc.data()?['familyId'] as String?;
      return familyId != null && familyId.isNotEmpty;
    } catch (e) {
      throw ServerException('Failed to check family membership: $e');
    }
  }

  @override
  Future<String> generateInviteCode(String familyId) async {
    try {
      // Generate a random 8-character alphanumeric code
      const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
      final random = DateTime.now().millisecondsSinceEpoch;
      final code = String.fromCharCodes(
        Iterable.generate(
          8,
          (index) => chars.codeUnitAt((random + index) % chars.length),
        ),
      );

      // Save the invite code
      await firestore.collection('families').doc(familyId).update({
        'inviteCode': code,
        'updatedAt': FieldValue.serverTimestamp(),
      });

      return code;
    } catch (e) {
      throw ServerException('Failed to generate invite code: $e');
    }
  }

  @override
  Future<bool> validateInviteCode(String inviteCode) async {
    try {
      final result = await firestore
          .collection('families')
          .where('inviteCode', isEqualTo: inviteCode)
          .limit(1)
          .get();

      return result.docs.isNotEmpty;
    } catch (e) {
      throw ServerException('Failed to validate invite code: $e');
    }
  }
}

