import 'dart:async';
import 'package:connectivity_plus/connectivity_plus.dart';
import 'package:dartz/dartz.dart';
import 'package:family_tree_firebase/core/error/failures.dart';
import 'package:family_tree_firebase/core/network/network_info.dart';

class NetworkInfoImpl implements NetworkInfo {
  final Connectivity connectivity;
  final StreamController<bool> _connectionStatusController = StreamController<bool>.broadcast();

  NetworkInfoImpl({required this.connectivity}) {
    // Listen to connectivity changes
    connectivity.onConnectivityChanged.listen((ConnectivityResult result) {
      _connectionStatusController.add(_isConnected(result));
    });
  }

  @override
  Future<bool> get isConnected async {
    final connectivityResult = await connectivity.checkConnectivity();
    return _isConnected(connectivityResult);
  }

  @override
  Future<Either<Failure, bool>> isConnectedWithFailure() async {
    try {
      final isConnected = await this.isConnected;
      return Right(isConnected);
    } catch (e) {
      return Left(NetworkFailure('Network connectivity check failed'));
    }
  }

  bool _isConnected(ConnectivityResult result) {
    return result == ConnectivityResult.wifi ||
        result == ConnectivityResult.ethernet ||
        result == ConnectivityResult.mobile ||
        result == ConnectivityResult.vpn;
  }

  void dispose() {
    _connectionStatusController.close();
  }
}

import 'dart:async';

import 'package:firebase_auth/firebase_auth.dart';
