import 'package:family_tree_firebase/features/auth/domain/entities/user_entity.dart';
import 'package:family_tree_firebase/features/auth/domain/failures/auth_failure.dart';
import 'package:family_tree_firebase/features/auth/domain/repositories/auth_repository.dart';

class AuthRepositoryImpl implements AuthRepository {
  final AuthRemoteDataSource remoteDataSource;

  AuthRepositoryImpl({required this.remoteDataSource});

  @override
  Future<bool> isSignedIn() async {
    try {
      return await remoteDataSource.isSignedIn();
    } catch (e) {
      return false;
    }
  }

  @override
  Future<Option<UserEntity>> getCurrentUser() async {
    try {
      final user = await remoteDataSource.getCurrentUser();
      if (user == null) {
        return const None();
      }
      return optionOf(user.toEntity());
    } catch (e) {
      return const None();
    }
  }

  @override
  Future<Either<AuthFailure, Unit>> signInWithEmailAndPassword({
    required String email,
    required String password,
  }) async {
    try {
      await remoteDataSource.signInWithEmailAndPassword(
        email: email,
        password: password,
      );
      return const Right(unit);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } catch (e) {
      return const Left(UnexpectedFailure());
    }
  }

  @override
  Future<Either<AuthFailure, Unit>> registerWithEmailAndPassword({
    required String email,
    required String password,
    required String displayName,
    String? phoneNumber,
  }) async {
    try {
      await remoteDataSource.registerWithEmailAndPassword(
        email: email,
        password: password,
        displayName: displayName,
        phoneNumber: phoneNumber,
      );
      return const Right(unit);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } catch (e) {
      return const Left(UnexpectedFailure());
    }
  }

  @override
  Future<Either<AuthFailure, Unit>> signInWithGoogle() async {
    try {
      await remoteDataSource.signInWithGoogle();
      return const Right(unit);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } on Exception {
      return const Left(UnexpectedFailure());
    }
  }

  @override
  Future<Either<AuthFailure, Unit>> signInWithApple() async {
    try {
      await remoteDataSource.signInWithApple();
      return const Right(unit);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } on Exception {
      return const Left(UnexpectedFailure());
    }
  }

  @override
  Future<Either<AuthFailure, String>> sendOtpToPhoneNumber({
    required String phoneNumber,
  }) async {
    try {
      final verificationId = await remoteDataSource.sendOtpToPhoneNumber(
        phoneNumber: phoneNumber,
      );
      return Right(verificationId);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } on Exception {
      return const Left(UnexpectedFailure());
    }
  }

  @override
  Future<Either<AuthFailure, Unit>> verifyOtp({
    required String verificationId,
    required String smsCode,
  }) async {
    try {
      await remoteDataSource.verifyOtp(
        verificationId: verificationId,
        smsCode: smsCode,
      );
      return const Right(unit);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } on Exception {
      return const Left(UnexpectedFailure());
    }
  }

  @override
  Future<Either<AuthFailure, Unit>> signOut() async {
    try {
      await remoteDataSource.signOut();
      return const Right(unit);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } on Exception {
      return const Left(UnexpectedFailure());
    }
  }

  @override
  Future<Either<AuthFailure, String>> getAuthToken() async {
    try {
      final token = await remoteDataSource.getAuthToken();
      return Right(token);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } on Exception {
      return const Left(UnexpectedFailure());
    }
  }

  @override
  Future<Either<AuthFailure, Unit>> updateUserProfile({
    String? displayName,
    String? photoUrl,
    String? phoneNumber,
  }) async {
    try {
      await remoteDataSource.updateUserProfile(
        displayName: displayName,
        photoUrl: photoUrl,
        phoneNumber: phoneNumber,
      );
      return const Right(unit);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } on Exception {
      return const Left(UnexpectedFailure());
    }
  }

  @override
  Future<Either<AuthFailure, Unit>> sendPasswordResetEmail(String email) async {
    try {
      await remoteDataSource.sendPasswordResetEmail(email);
      return const Right(unit);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } on Exception {
      return const Left(UnexpectedFailure());
    }
  }

  @override
  Future<Either<AuthFailure, Unit>> deleteAccount() async {
    try {
      await remoteDataSource.deleteAccount();
      return const Right(unit);
    } on FirebaseAuthException catch (e) {
      return Left(_handleFirebaseAuthException(e));
    } on Exception {
      return const Left(UnexpectedFailure());
    }
  }

  AuthFailure _handleFirebaseAuthException(FirebaseAuthException e) {
    switch (e.code) {
      case 'invalid-email':
        return const InvalidEmailFailure();
      case 'user-disabled':
        return const UserDisabledFailure();
      case 'user-not-found':
        return const UserNotFoundFailure();
      case 'wrong-password':
        return const InvalidPasswordFailure();
      case 'email-already-in-use':
        return const EmailAlreadyInUseFailure();
      case 'operation-not-allowed':
      case 'account-exists-with-different-credential':
      case 'auth-domain-config-required':
      case 'cancelled-popup-request':
      case 'popup-blocked':
      case 'popup-closed-by-user':
      case 'unauthorized-domain':
      case 'web-storage-unsupported':
        return const UnexpectedFailure();
      case 'invalid-verification-code':
        return const InvalidVerificationCodeFailure();
      case 'invalid-verification-id':
      case 'session-expired':
        return const SessionExpiredFailure();
      case 'network-request-failed':
        return const NetworkFailure();
      default:
        return const UnexpectedFailure();
    }
  }
}

