final sl = GetIt.instance;

@injectableInit
Future<void> initHomeFeature() async {
  // Register data sources
  if (!sl.isRegistered<PostRemoteDataSource>()) {
    sl.registerLazySingleton<PostRemoteDataSource>(
      () => PostRemoteDataSourceImpl(
        firestore: sl<FirebaseFirestore>(),
        firebaseAuth: sl<FirebaseAuth>(),
        networkInfo: sl<NetworkInfo>(),
      ),
    );
  }

  if (!sl.isRegistered<StoryRemoteDataSource>()) {
    sl.registerLazySingleton<StoryRemoteDataSource>(
      () => StoryRemoteDataSourceImpl(
        firestore: sl<FirebaseFirestore>(),
        firebaseAuth: sl<FirebaseAuth>(),
        networkInfo: sl<NetworkInfo>(),
      ),
    );
  }

  // Register repositories
  if (!sl.isRegistered<PostRepository>()) {
    sl.registerLazySingleton<PostRepository>(
      () => PostRepositoryImpl(
        remoteDataSource: sl<PostRemoteDataSource>(),
        networkInfo: sl<NetworkInfo>(),
      ),
    );
  }

  if (!sl.isRegistered<StoryRepository>()) {
    sl.registerLazySingleton<StoryRepository>(
      () => StoryRepositoryImpl(
        remoteDataSource: sl<StoryRemoteDataSource>(),
        networkInfo: sl<NetworkInfo>(),
      ),
    );
  }

  // Register use cases
  if (!sl.isRegistered<GetPosts>()) {
    sl.registerLazySingleton(() => GetPosts(sl<PostRepository>()));
  }

  if (!sl.isRegistered<GetStories>()) {
    sl.registerLazySingleton(() => GetStories(sl<StoryRepository>()));
  }

  if (!sl.isRegistered<LikePost>()) {
    sl.registerLazySingleton(() => LikePost(sl<PostRepository>()));
  }

  if (!sl.isRegistered<AddComment>()) {
    sl.registerLazySingleton(() => AddComment(sl<PostRepository>()));
  }

  // Register ViewModel
  if (!sl.isRegistered<HomeViewModel>()) {
    sl.registerFactory(
      () => HomeViewModel(
        getPosts: sl<GetPosts>(),
        getStories: sl<GetStories>(),
        likePostUseCase: sl<LikePost>(),
        addCommentUseCase: sl<AddComment>(),
      ),
    );
  }
}

import 'package:cloud_firestore/cloud_firestore.dart';
import 'package:dartz/dartz.dart';
import 'package:family_tree_firebase/core/error/exceptions.dart';
import 'package:family_tree_firebase/features/home/data/datasources/exceptions.dart';
import 'package:family_tree_firebase/core/network/network_info.dart';
import 'package:family_tree_firebase/features/home/domain/entities/comment.dart';
import 'package:family_tree_firebase/features/home/domain/entities/post.dart';
import 'package:firebase_auth/firebase_auth.dart';
import 'package:injectable/injectable.dart';

abstract class PostRemoteDataSource {
  Future<List<Post>> getPosts({
    String? familyId,
    int limit = 10,
    String? lastPostId,
  });

  Future<Unit> likePost({
    required String postId,
    required String userId,
  });

  Future<Comment> addComment({
    required String postId,
    required String userId,
    required String content,
    required String userName,
    String? userAvatar,
  });

  Future<List<Comment>> getComments({
    required String postId,
    int limit = 20,
    String? lastCommentId,
  });

  Future<Post> createPost({
    required String userId,
    required String content,
    required String userName,
    String? userAvatar,
    List<String>? imageUrls,
    String? familyId,
  });

  Future<Unit> deletePost({
    required String postId,
    required String userId,
  });
}

@LazySingleton(as: PostRemoteDataSource)
class PostRemoteDataSourceImpl implements PostRemoteDataSource {
  final FirebaseFirestore firestore;
  final FirebaseAuth firebaseAuth;
  final NetworkInfo networkInfo;

  PostRemoteDataSourceImpl({
    required this.firestore,
    required this.firebaseAuth,
    required this.networkInfo,
  });

  @override
  Future<List<Post>> getPosts({
    String? familyId,
    int limit = 10,
    String? lastPostId,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      Query postsQuery = firestore
          .collection('posts')
          .orderBy('timestamp', descending: true)
          .limit(limit);

      if (familyId != null) {
        postsQuery = postsQuery.where('familyId', isEqualTo: familyId);
      }

      if (lastPostId != null) {
        final lastDoc = await firestore.collection('posts').doc(lastPostId).get();
        postsQuery = postsQuery.startAfterDocument(lastDoc);
      }

      final querySnapshot = await postsQuery.get();
      final currentUserId = firebaseAuth.currentUser?.uid;

      return querySnapshot.docs.map((doc) {
        final data = doc.data() as Map<String, dynamic>;
        return Post(
          id: doc.id,
          userId: data['userId'],
          userName: data['userName'],
          userAvatar: data['userAvatar'],
          content: data['content'] ?? '',
          imageUrls: List<String>.from(data['imageUrls'] ?? []),
          timestamp: (data['timestamp'] as Timestamp).toDate(),
          likes: List<String>.from(data['likes'] ?? []),
          commentCount: data['commentCount'] ?? 0,
          familyId: data['familyId'],
          isLiked: currentUserId != null && (data['likes'] as List<dynamic>).contains(currentUserId),
          isPinned: data['isPinned'] ?? false,
          isReported: data['isReported'] ?? false,
          updatedAt: data['updatedAt']?.toDate(),
        );
      }).toList();
    } catch (e) {
      throw ServerException(message: 'Failed to fetch posts: $e');
    }
  }

  @override
  Future<Unit> likePost({
    required String postId,
    required String userId,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      final postRef = firestore.collection('posts').doc(postId);
      await firestore.runTransaction((transaction) async {
        final postDoc = await transaction.get(postRef);
        if (!postDoc.exists) {
          throw NotFoundException('Post not found');
        }

        final data = postDoc.data()!;
        final likes = List<String>.from(data['likes'] ?? []);
        final isLiked = likes.contains(userId);

        if (isLiked) {
          likes.remove(userId);
        } else {
          likes.add(userId);
        }

        transaction.update(postRef, {
          'likes': likes,
          'updatedAt': FieldValue.serverTimestamp(),
        });
      });
      return unit;
    } catch (e) {
      throw ServerException(message: 'Failed to like post: $e');
    }
  }

  @override
  Future<Comment> addComment({
    required String postId,
    required String userId,
    required String content,
    required String userName,
    String? userAvatar,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      final commentRef = firestore
          .collection('posts')
          .doc(postId)
          .collection('comments')
          .doc();

      final commentData = {
        'id': commentRef.id,
        'postId': postId,
        'userId': userId,
        'userName': userName,
        'userAvatar': userAvatar,
        'content': content,
        'timestamp': FieldValue.serverTimestamp(),
        'likes': [],
        'isEdited': false,
        'parentCommentId': null,
        'replies': [],
        'replyCount': 0,
      };

      await firestore.runTransaction((transaction) async {
        // Add the comment
        transaction.set(commentRef, commentData);

        // Increment the comment count on the post
        final postRef = firestore.collection('posts').doc(postId);
        transaction.update(postRef, {
          'commentCount': FieldValue.increment(1),
          'updatedAt': FieldValue.serverTimestamp(),
        });
      });

      return Comment(
        id: commentRef.id,
        postId: postId,
        userId: userId,
        userName: userName,
        userAvatar: userAvatar,
        content: content,
        timestamp: DateTime.now(),
        likes: [],
        isLiked: false,
        isEdited: false,
        parentCommentId: null,
        replies: [],
        replyCount: 0,
      );
    } catch (e) {
      throw ServerException(message: 'Failed to add comment: $e');
    }
  }

  @override
  Future<List<Comment>> getComments({
    required String postId,
    int limit = 20,
    String? lastCommentId,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      var query = firestore
          .collection('posts')
          .doc(postId)
          .collection('comments')
          .orderBy('timestamp', descending: true)
          .limit(limit);

      if (lastCommentId != null) {
        final lastDoc = await firestore
            .collection('posts')
            .doc(postId)
            .collection('comments')
            .doc(lastCommentId)
            .get();
        query = query.startAfterDocument(lastDoc);
      }

      final querySnapshot = await query.get();
      final currentUserId = firebaseAuth.currentUser?.uid;

      return querySnapshot.docs.map((doc) {
        final data = doc.data();
        return Comment(
          id: doc.id,
          postId: data['postId'],
          userId: data['userId'],
          userName: data['userName'],
          userAvatar: data['userAvatar'],
          content: data['content'],
          timestamp: (data['timestamp'] as Timestamp).toDate(),
          likes: List<String>.from(data['likes'] ?? []),
          isLiked: currentUserId != null && (data['likes'] as List<dynamic>).contains(currentUserId),
          isEdited: data['isEdited'] ?? false,
          parentCommentId: data['parentCommentId'],
          replies: List<String>.from(data['replies'] ?? []),
          replyCount: data['replyCount'] ?? 0,
        );
      }).toList();
    } catch (e) {
      throw ServerException(message: 'Failed to fetch comments: $e');
    }
  }

  @override
  Future<Post> createPost({
    required String userId,
    required String content,
    required String userName,
    String? userAvatar,
    List<String>? imageUrls,
    String? familyId,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      final postRef = firestore.collection('posts').doc();
      final timestamp = FieldValue.serverTimestamp();

      final postData = {
        'id': postRef.id,
        'userId': userId,
        'userName': userName,
        'userAvatar': userAvatar,
        'content': content,
        'imageUrls': imageUrls ?? [],
        'timestamp': timestamp,
        'likes': [],
        'commentCount': 0,
        'familyId': familyId,
        'isPinned': false,
        'isReported': false,
        'updatedAt': timestamp,
      };

      await postRef.set(postData);

      return Post(
        id: postRef.id,
        userId: userId,
        userName: userName,
        userAvatar: userAvatar,
        content: content,
        imageUrls: imageUrls ?? [],
        timestamp: DateTime.now(),
        likes: [],
        commentCount: 0,
        familyId: familyId,
        isLiked: false,
        isPinned: false,
        isReported: false,
        updatedAt: DateTime.now(),
      );
    } catch (e) {
      throw ServerException(message: 'Failed to create post: $e');
    }
  }

  @override
  Future<Unit> deletePost({
    required String postId,
    required String userId,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      final postRef = firestore.collection('posts').doc(postId);

      await firestore.runTransaction((transaction) async {
        final postDoc = await transaction.get(postRef);
        if (!postDoc.exists) {
          throw NotFoundException('Post not found');
        }

        final data = postDoc.data()!;
        if (data['userId'] != userId) {
          throw const UnauthorizedException('Not authorized to delete this post');
        }

        // Delete the post
        transaction.delete(postRef);

        // Optionally: Delete all comments for this post
        // Note: This might be better handled with a Cloud Function
        // to avoid long-running transactions
        final commentsSnapshot = await postRef.collection('comments').get();
        for (var doc in commentsSnapshot.docs) {
          transaction.delete(doc.reference);
        }
      });

      return unit;
    } catch (e) {
      throw ServerException(message: 'Failed to delete post: $e');
    }
  }
}

import 'package:cloud_firestore/cloud_firestore.dart';
import 'package:dartz/dartz.dart';
import 'package:family_tree_firebase/features/home/data/datasources/exceptions.dart';
import 'package:family_tree_firebase/core/network/network_info.dart';
import 'package:family_tree_firebase/features/home/domain/entities/story.dart';
import 'package:firebase_auth/firebase_auth.dart';
import 'package:injectable/injectable.dart';

abstract class StoryRemoteDataSource {
  Future<List<Story>> getStories({
    String? familyId,
    int limit = 20,
    String? lastStoryId,
  });

  Future<Story> createStory({
    required String userId,
    required String userName,
    String? userAvatar,
    required List<String> mediaUrls,
    String? caption,
    Duration? duration,
    String? familyId,
  });

  Future<Unit> viewStory({
    required String storyId,
    required String userId,
  });

  Future<Unit> deleteStory({
    required String storyId,
    required String userId,
  });

  Future<Unit> reportStory({
    required String storyId,
    required String userId,
    required String reason,
  });

  Future<List<Story>> getUserStories({
    required String userId,
    int limit = 20,
    String? lastStoryId,
  });
}

@LazySingleton(as: StoryRemoteDataSource)
class StoryRemoteDataSourceImpl implements StoryRemoteDataSource {
  final FirebaseFirestore firestore;
  final FirebaseAuth firebaseAuth;
  final NetworkInfo networkInfo;

  StoryRemoteDataSourceImpl({
    required this.firestore,
    required this.firebaseAuth,
    required this.networkInfo,
  });

  @override
  Future<List<Story>> getStories({
    String? familyId,
    int limit = 20,
    String? lastStoryId,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      Query storiesQuery = firestore
          .collection('stories')
          .where('timestamp', isGreaterThan: _get24HoursAgo())
          .orderBy('timestamp', descending: true)
          .limit(limit);

      if (familyId != null) {
        storiesQuery = storiesQuery.where('familyId', isEqualTo: familyId);
      }

      if (lastStoryId != null) {
        final lastDoc = await firestore.collection('stories').doc(lastStoryId).get();
        storiesQuery = storiesQuery.startAfterDocument(lastDoc);
      }

      final querySnapshot = await storiesQuery.get();
      final currentUserId = firebaseAuth.currentUser?.uid;

      return querySnapshot.docs.map((doc) {
        final data = doc.data() as Map<String, dynamic>;
        return _mapDocumentToStory(doc.id, data, currentUserId);
      }).toList();
    } catch (e) {
      throw ServerException(message: 'Failed to fetch stories: $e');
    }
  }

  @override
  Future<Story> createStory({
    required String userId,
    required String userName,
    String? userAvatar,
    required List<String> mediaUrls,
    String? caption,
    Duration? duration,
    String? familyId,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      final storyRef = firestore.collection('stories').doc();
      final timestamp = FieldValue.serverTimestamp();

      final storyData = {
        'id': storyRef.id,
        'userId': userId,
        'userName': userName,
        'userAvatar': userAvatar,
        'media': mediaUrls.map((url) => {
          'url': url,
          'type': _getMediaType(url),
          'duration': duration?.inSeconds,
        }).toList(),
        'caption': caption,
        'timestamp': timestamp,
        'viewers': [],
        'viewCount': 0,
        'familyId': familyId,
        'isArchived': false,
        'isReported': false,
      };

      await storyRef.set(storyData);

      return Story(
        id: storyRef.id,
        userId: userId,
        userName: userName,
        userAvatar: userAvatar,
        media: mediaUrls.map((url) => StoryMedia(
          url: url,
          type: _getMediaType(url),
          duration: duration,
        )).toList(),
        caption: caption,
        timestamp: DateTime.now(),
        viewers: [],
        viewCount: 0,
        isViewed: false,
        familyId: familyId,
        isArchived: false,
        isReported: false,
      );
    } catch (e) {
      throw ServerException(message: 'Failed to create story: $e');
    }
  }

  @override
  Future<Unit> viewStory({
    required String storyId,
    required String userId,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      final storyRef = firestore.collection('stories').doc(storyId);

      await firestore.runTransaction((transaction) async {
        final storyDoc = await transaction.get(storyRef);
        if (!storyDoc.exists) {
          throw NotFoundException('Story not found');
        }

        final data = storyDoc.data()!;
        final viewers = List<String>.from(data['viewers'] ?? []);

        if (!viewers.contains(userId)) {
          viewers.add(userId);
          transaction.update(storyRef, {
            'viewers': viewers,
            'viewCount': FieldValue.increment(1),
          });
        }
      });

      return unit;
    } catch (e) {
      throw ServerException(message: 'Failed to view story: $e');
    }
  }

  @override
  Future<Unit> deleteStory({
    required String storyId,
    required String userId,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      final storyRef = firestore.collection('stories').doc(storyId);

      await firestore.runTransaction((transaction) async {
        final storyDoc = await transaction.get(storyRef);
        if (!storyDoc.exists) {
          throw NotFoundException('Story not found');
        }

        final data = storyDoc.data()!;
        if (data['userId'] != userId) {
          throw const UnauthorizedException('Not authorized to delete this story');
        }

        transaction.delete(storyRef);
      });

      return unit;
    } catch (e) {
      throw ServerException(message: 'Failed to delete story: $e');
    }
  }

  @override
  Future<Unit> reportStory({
    required String storyId,
    required String userId,
    required String reason,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      final reportRef = firestore.collection('reports').doc();

      await reportRef.set({
        'id': reportRef.id,
        'type': 'story',
        'targetId': storyId,
        'reporterId': userId,
        'reason': reason,
        'timestamp': FieldValue.serverTimestamp(),
        'status': 'pending',
      });

      // Mark the story as reported
      await firestore.collection('stories').doc(storyId).update({
        'isReported': true,
      });

      return unit;
    } catch (e) {
      throw ServerException(message: 'Failed to report story: $e');
    }
  }

  @override
  Future<List<Story>> getUserStories({
    required String userId,
    int limit = 20,
    String? lastStoryId,
  }) async {
    if (!await networkInfo.isConnected) {
      throw NetworkException();
    }

    try {
      Query storiesQuery = firestore
          .collection('stories')
          .where('userId', isEqualTo: userId)
          .orderBy('timestamp', descending: true)
          .limit(limit);

      if (lastStoryId != null) {
        final lastDoc = await firestore.collection('stories').doc(lastStoryId).get();
        storiesQuery = storiesQuery.startAfterDocument(lastDoc);
      }

      final querySnapshot = await storiesQuery.get();
      final currentUserId = firebaseAuth.currentUser?.uid;

      return querySnapshot.docs.map((doc) {
        final data = doc.data() as Map<String, dynamic>;
        return _mapDocumentToStory(doc.id, data, currentUserId);
      }).toList();
    } catch (e) {
      throw ServerException(message: 'Failed to fetch user stories: $e');
    }
  }

  // Helper methods
  String _getMediaType(String url) {
    final extension = url.split('.').last.toLowerCase();
    if (['jpg', 'jpeg', 'png', 'gif', 'webp'].contains(extension)) {
      return 'image';
    } else if (['mp4', 'mov', 'avi', 'webm'].contains(extension)) {
      return 'video';
    }
    return 'unknown';
  }

  DateTime _get24HoursAgo() {
    return DateTime.now().subtract(const Duration(hours: 24));
  }

  Story _mapDocumentToStory(String id, Map<String, dynamic> data, String? currentUserId) {
    final media = (data['media'] as List<dynamic>?)?.map((item) {
      return StoryMedia(
        url: item['url'] as String,
        type: item['type'] as String? ?? 'image', // Default to 'image' if type is not specified
        duration: item['duration'] != null 
            ? Duration(seconds: (item['duration'] as num).toInt()) 
            : null,
        thumbnailUrl: item['thumbnailUrl'] as String?,
      );
    }).toList() ?? [];

    final viewers = List<String>.from(data['viewers'] ?? []);
    final timestamp = (data['timestamp'] as Timestamp).toDate();

    return Story(
      id: id,
      userId: data['userId'] as String,
      userName: data['userName'] as String,
      userAvatar: data['userAvatar'] as String?,
      media: media,
      caption: data['caption'] as String?,
      timestamp: timestamp,
      viewers: viewers,
      viewCount: (data['viewCount'] as num?)?.toInt() ?? 0,
      isViewed: currentUserId != null && viewers.contains(currentUserId),
      familyId: data['familyId'] as String?,
      isArchived: data['isArchived'] as bool? ?? false,
      isReported: data['isReported'] as bool? ?? false,
    );
  }
}

import 'package:dartz/dartz.dart';
import 'package:family_tree_firebase/core/error/exceptions.dart';
