import 'package:injectable/injectable.dart';

@LazySingleton(as: PostRepository)
class PostRepositoryImpl implements PostRepository {
  final PostRemoteDataSource remoteDataSource;
  final NetworkInfo networkInfo;

  PostRepositoryImpl({
    required this.remoteDataSource,
    required this.networkInfo,
  });

  @override
  Future<Either<Failure, List<Post>>> getPosts({
    String? familyId,
    int limit = 10,
    String? lastPostId,
  }) async {
    if (await networkInfo.isConnected) {
      try {
        final posts = await remoteDataSource.getPosts(
          familyId: familyId,
          limit: limit,
          lastPostId: lastPostId,
        );
        return Right(posts);
      } on ServerException catch (e, stackTrace) {
        return Left(ServerFailure(e.message, stackTrace));
      } on NetworkException catch (_, stackTrace) {
        return Left(const NetworkFailure('No internet connection'), stackTrace);
      } catch (e, stackTrace) {
        return Left(ServerFailure('Unexpected error: $e', stackTrace));
      }
    } else {
      return const Left(NetworkFailure(message: 'No internet connection'));
    }
  }

  @override
  Future<Either<Failure, Unit>> likePost({
    required String postId,
    required String userId,
  }) async {
    if (await networkInfo.isConnected) {
      try {
        await remoteDataSource.likePost(
          postId: postId,
          userId: userId,
        );
        return const Right(unit);
      } on ServerException catch (e) {
        return Left(ServerFailure(message: e.message));
      } on NetworkException {
        return const Left(NetworkFailure(message: 'No internet connection'));
      } catch (e) {
        return Left(ServerFailure(message: 'Failed to like post: $e'));
      }
    } else {
      return const Left(NetworkFailure(message: 'No internet connection'));
