    required String content,
    required String userName,
    String? userAvatar,
  }) async {
    if (await networkInfo.isConnected) {
      try {
        final comment = await remoteDataSource.addComment(
          postId: postId,
          userId: userId,
          content: content,
          userName: userName,
          userAvatar: userAvatar,
        );
        return Right(comment);
      } on ServerException catch (e) {
        return Left(ServerFailure(message: e.message));
      } on NetworkException {
        return const Left(NetworkFailure(message: 'No internet connection'));
      } catch (e) {
        return Left(ServerFailure(message: 'Failed to add comment: $e'));
      }
    } else {
      return const Left(NetworkFailure(message: 'No internet connection'));
    }
  }

  @override
  Future<Either<Failure, List<Comment>>> getComments({
    required String postId,
    int limit = 20,
    String? lastCommentId,
  }) async {
    if (await networkInfo.isConnected) {
      try {
        final comments = await remoteDataSource.getComments(
          postId: postId,
          limit: limit,
          lastCommentId: lastCommentId,
        );
        return Right(comments);
      } on ServerException catch (e) {
        return Left(ServerFailure(message: e.message));
      } on NetworkException {
        return const Left(NetworkFailure(message: 'No internet connection'));
      } catch (e) {
        return Left(ServerFailure(message: 'Failed to get comments: $e'));
      }
    } else {
      return const Left(NetworkFailure(message: 'No internet connection'));
    }
  }

  @override
  Future<Either<Failure, Post>> createPost({
    required String userId,
    required String content,
    required String userName,
    String? userAvatar,
    List<String>? imageUrls,
    String? familyId,
  }) async {
    if (await networkInfo.isConnected) {
      try {
        final post = await remoteDataSource.createPost(
          userId: userId,
          content: content,
          userName: userName,
          userAvatar: userAvatar,
          imageUrls: imageUrls,
          familyId: familyId,
        );
        return Right(post);
      } on ServerException catch (e) {
        return Left(ServerFailure(message: e.message));
      } on NetworkException {
        return const Left(NetworkFailure(message: 'No internet connection'));
      } catch (e) {
        return Left(ServerFailure(message: 'Failed to create post: $e'));
      }
    } else {
      return const Left(NetworkFailure(message: 'No internet connection'));
    }
  }

  @override
  Future<Either<Failure, Unit>> deletePost({
    required String postId,
    required String userId,
  }) async {
    if (await networkInfo.isConnected) {
      try {
        await remoteDataSource.deletePost(
          postId: postId,
          userId: userId,
        );
        return const Right(unit);
      } on ServerException catch (e) {
        return Left(ServerFailure(message: e.message));
      } on NetworkException {
        return const Left(NetworkFailure(message: 'No internet connection'));
      } on UnauthorizedException {
        return const Left(UnauthorizedFailure(message: 'Not authorized to delete this post'));
      } catch (e) {
        return Left(ServerFailure(message: 'Failed to delete post: $e'));
      }
    } else {
      return const Left(NetworkFailure(message: 'No internet connection'));
    }
  }

  @override
  Future<Either<Failure, Unit>> reportPost({
    required String postId,
    required String userId,
    required String reason,
  }) async {
    // Implementation for reporting a post
    // This would typically involve creating a report in a separate collection
    // and possibly flagging the post for review
    return const Left(UnimplementedFailure(message: 'Report post not implemented'));
  }
}

import 'package:equatable/equatable.dart';

abstract class Failure extends Equatable {
  final String message;
  final StackTrace? stackTrace;

  const Failure(this.message, [this.stackTrace]);

  @override
  List<Object?> get props => [message, stackTrace];
}

// General failures
class ServerFailure extends Failure {
  const ServerFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

class CacheFailure extends Failure {
  const CacheFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

class NetworkFailure extends Failure {
  const NetworkFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

class InvalidInputFailure extends Failure {
  const InvalidInputFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

// Authentication failures
class AuthenticationFailure extends Failure {
  const AuthenticationFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

class InvalidCredentialsFailure extends Failure {
  const InvalidCredentialsFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

class EmailAlreadyInUseFailure extends Failure {
  const EmailAlreadyInUseFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

class UnauthorizedFailure extends Failure {
  const UnauthorizedFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

class WeakPasswordFailure extends Failure {
  const WeakPasswordFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

// Family failures
class FamilyNotFoundFailure extends Failure {
  const FamilyNotFoundFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

class InvalidInviteCodeFailure extends Failure {
  const InvalidInviteCodeFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

class AlreadyInFamilyFailure extends Failure {
  const AlreadyInFamilyFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

class PermissionDeniedFailure extends Failure {
  const PermissionDeniedFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

// Not found failure
class NotFoundFailure extends Failure {
  const NotFoundFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

// Validation failure
class ValidationFailure extends Failure {
  const ValidationFailure(String message, [StackTrace? stackTrace])
      : super(message, stackTrace);
}

